import { useRef } from 'react'
import styles from './Product.module.scss';
import { Icon } from '@iconify/react';
import shoppingCartOutlined from '@iconify-icons/ant-design/shopping-cart-outlined';

const Product = ({ img }) => {

    const refContainer = useRef(null);

    const addToCart = () => {
        console.log(Number(refContainer.current.value) === 0);
        if (refContainer.current.value !== "" && refContainer.current.value !== 0)
            console.log(refContainer.current.value);
    }
    return (
        <div className={styles.product}>
            <h3>products name</h3>
            <img src={img} alt="bike" />
            <div className={styles.productInfo}>
                <h4>price</h4>
                <p>short info</p>
                <div className={styles.adding}>
                    <input ref={refContainer} type="number" name="" id="" />
                    <button onClick={addToCart} >
                        <Icon icon={shoppingCartOutlined} width="25px" />
                        Add to cart
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Product;
